<?php

$input = '';

// Part 1
function findStartOfPacketMarker($input)
{
    $chars = str_split($input);
    $charsCount = count($chars);
    for ($i = 0; $i < $charsCount; $i++) {
        $currentChars = array_slice($chars, $i, 4);
        if (count(array_unique($currentChars)) === 4) {
            return $i + 4;
        }
    }
}

// Puzzle Input
echo "Part 1: " . findStartOfPacketMarker($input) . "\n";

// Part 2
function findStartOfMessageMarker($input)
{
    $chars = str_split($input);
    $charsCount = count($chars);
    for ($i = 0; $i < $charsCount; $i++) {
        $currentChars = array_slice($chars, $i, 14);
        if (count(array_unique($currentChars)) === 14) {
            return $i + 14;
        }
    }
}

echo "Part 2: " . findStartOfMessageMarker($input) . "\n";
